/**
 * @module Types / ListParams
 * @category Types
 */
import { BaseEntitySortableFields, DefaultSearchableFields, JwtConfig, SortOptions } from "../../../../types/src";
import { HeaderValue } from './headers';
/**
 * Defines the standard pagination parameters
 * supported by the ledger API.
 */
export type LedgerPaginationParams = {
    /**
     * Current page index, zero based.
     */
    index: number;
    /**
     * Page results limit, max page size.
     */
    limit: number;
};
export type BaseListParamsWithoutSearch = {
    authParams?: Partial<JwtConfig>;
    headers?: Record<string, HeaderValue>;
    page?: LedgerPaginationParams;
    sort?: SortOptions<BaseEntitySortableFields>;
};
export type BaseListParams = BaseListParamsWithoutSearch & DefaultSearchableFields;
