import { JwtConfig } from "../../../../types/src";
import { HeaderValue } from './headers';
/**
 * Options for customizing request sent to the Ledger API.
 */
export type RequestOptions = {
    /**
     * Authentication params responsible for composing JWT to be sent to the Ledger API.
     */
    authParams?: Partial<JwtConfig>;
    /**
     * Custom headers to be sent with the request. This will affect only this request.
     */
    headers?: Record<string, HeaderValue>;
};
