import { LedgerMeta } from '../common/ledger-meta';
import { LedgerList } from './ledger-list';
export type LedgerPagedList<TData extends Record<string, any>, TMeta extends LedgerMeta = LedgerMeta> = LedgerList<TData, TMeta> & {
    /**
     * Pagination information.
     */
    page: LedgerPage;
};
/**
 * Pagination information of ledger list responses.
 */
export type LedgerPage = {
    /**
     * Current page index, zero based.
     */
    index: number;
    /**
     * Max number of records per page.
     */
    limit: number;
    /**
     * Total results of the query, this property is optional,
     * it can be very expensive to calculate it for complex
     * queries.
     */
    total?: number;
};
