import { LedgerErrorReason } from "../../../../errors/src";
import { LedgerCustom } from './ledger-custom';
/**
 * Type for error `data` sent in error response records.
 * Error response has standard structure with:
 * - hash
 * - data:
 * - meta
 */
export type ErrorData<TCustom extends LedgerCustom = LedgerCustom> = {
    reason: LedgerErrorReason;
    detail: string;
    custom?: TCustom;
};
