import { AddToSet, Pop, Pull, PullAll, Push } from './aggregation';
/**
 * Label assigned to a record
 */
export type LedgerLabel = string;
/**
 * Appends labels specified
 */
export type LedgerLabelPush = Push<LedgerLabel>;
/**
 * Appends labels specified - unless they are already present
 */
export type LedgerLabelAddToSet = AddToSet<LedgerLabel>;
/**
 * Removes labels specified
 */
export type LedgerLabelPull = Pull<LedgerLabel>;
/**
 * Removes all the labels specified
 */
export type LedgerLabelPullAll = PullAll<LedgerLabel>;
/**
 * Label query combinations
 */
export type LedgerLabelQuery = LedgerLabel[] | LedgerLabelPush | LedgerLabelAddToSet | LedgerLabelPull | LedgerLabelPullAll | Pop;
