import { LedgerCustom } from './ledger-custom';
import { LedgerHandle } from './ledger-handle';
/**
 * A ledger reference which can be used to identify
 * a ledger record. The reference's handle is a unique
 * identifier of a ledger record. The reference can
 * also contain custom data.
 */
export type LedgerReference<THandle = LedgerHandle> = {
    handle: THandle;
    custom?: LedgerCustom;
};
