import { InAggregation } from '../common/aggregation';
import { AccessAction, AccessRecord } from '../common/ledger-access';
export type ActionCheck = AccessAction | InAggregation<AccessAction>;
export type RecordCheck = AccessRecord | InAggregation<AccessRecord>;
export type AccessCheck = {
    /**
     * Action to be checked
     */
    action: ActionCheck;
    /**
     * Record to be checked.
     * The record is implicitly inferred
     * when it's empty.
     */
    record?: RecordCheck;
};
