import { LedgerHash } from '../common/ledger-hash';
/**
 * Query to reveal the secrets of a record based on their
 * unique identifiers.
 */
export type SecretRevealQuery = {
    /**
     * The hash of the record to reveal secrets for. This
     * ensure uniqueness in the query.
     */
    parent: LedgerHash;
    /**
     * List of secrets to be revealed.
     */
    secret: string[];
};
