import { LedgerAddress } from '../common/ledger-address';
import { LedgerHandle } from '../common/ledger-handle';
/**
 * Represents a balance information of a ledger record.
 * Balances are currently stored only in ledger wallets.
 */
export type LedgerBalance = {
    /**
     * A wallet this balance belongs to.
     *
     * @see LedgerWallet
     */
    wallet: LedgerAddress;
    /**
     * A symbol or unit of the balance entry.
     *
     * @see LedgerSymbol
     */
    symbol: LedgerHandle;
    /**
     * Balance amount of the given symbol.
     */
    amount: number;
};
