import { LedgerAddress } from '../common/ledger-address';
import { LedgerHandle } from '../common/ledger-handle';
import { LimitClaimMetric } from './ledger-intent';
/**
 * Represents a limit information of a ledger record.
 * Limits are currently applied only for ledger wallets.
 */
export type LedgerLimit = {
    /**
     * A wallet this limit applies to.
     *
     * @see LedgerWallet
     */
    wallet: LedgerAddress;
    /**
     * A symbol or unit of the limit.
     *
     * @see LedgerSymbol
     */
    symbol: LedgerHandle;
    /**
     * Metric of the limit.
     */
    metric: LimitClaimMetric;
    /**
     * Limit amount of the given symbol.
     */
    amount: number;
};
