/**
 * Represents a basic status metadata about a ledger dependencies.
 */
export type LedgerStatusDependencies = {
    /**
     * Database status, provides information if the
     * database is CONNECTED or there is ERROR with
     * connection (error is followed by message).
     */
    db: 'CONNECTED' | 'ERROR';
    /**
     * Redis status, provides information if the
     * redis is CONNECTED or there is ERROR with
     * connection (error is followed by message).
     */
    redis: 'CONNECTED' | 'ERROR' | 'DISABLED';
};
/**
 * Represents a basic metadata about a ledger instance.
 * This object can be used to identify a ledger instance
 * or check the status of an instance.
 */
export type LedgerStatus = {
    /**
     * Instance name.
     */
    handle: string;
    /**
     * Instance base URL, i.e. URL without a ledger subdomain.
     */
    server: string;
    /**
     * Semantic version of a instance.
     */
    semver: string;
    /**
     * A name of the ledger instance, should
     * be unique for each ledger.
     */
    ledger: string;
    /**
     * Instance status, provides information if the
     * instance is UP or DOWN.
     */
    status: 'UP' | 'DOWN';
    /**
     * Instance uptime, in seconds.
     */
    uptime: number;
    /**
     * Status of ledger instance dependencies
     */
    dependencies: LedgerStatusDependencies;
};
